function dz=divide_double_pumped_fnp(~,y)
global h c  Ner Nyb sgm56p sgm65p sgm12 sgm21 sgm56 sgm65 ... 
   lams1 lams2 alpha1 alpha2 alphap Ts1 Ts2 Tp N2 N3 N6 N6np
  
 n2=N2;
 n3=N3;
 n1=Ner-n2-n3;
 n6=N6+N6np;
 n5=Nyb-n6;
 
dz=zeros(6,1);
 dz(1)=(Ts1*(n2*sgm21-n1*sgm12)-alpha1)*y(1)+Ts1*2*h*c^2/lams1^3*n2*sgm21;
 dz(2)=-(Ts1*(n2*sgm21-n1*sgm12)-alpha1)*y(2)-Ts1*2*h*c^2/lams1^3*n2*sgm21;
 dz(3)=(Ts2*(n6*sgm65-n5*sgm56)-alpha2)*y(3)+Ts2*2*h*c^2/lams2^3*n6*sgm65;
 dz(4)=-(Ts2*(n6*sgm65-n5*sgm56)-alpha2)*y(4)-Ts2*2*h*c^2/lams2^3*n6*sgm65;
 dz(5)=Tp*(n6*sgm65p-n5*sgm56p-alphap)*y(5);
 dz(6)=-Tp*(n6*sgm65p-n5*sgm56p-alphap)*y(6);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
 %dz=[dz(1);dz(2);dz(3);dz(4);dz(5);dz(6)];